package day3;

public class Program7_Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int num=1; num<=10; num++)
		{
			System.out.print(num+" ");
		}
		System.out.println();
		
		for(int num1=10;num1>=0;num1--)
		{
			System.out.print(num1+" ");
		}
	}

}
